import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { InvoiceService } from '../../services/invoice.service';
import { CreateInvoiceDto } from '../../dtos/create-invoice.dto'; 
import { Router } from '@angular/router';

@Component({
  selector: 'app-invoice-create',
  templateUrl: './invoice-create.component.html',
  styleUrls: ['./invoice-create.component.css']
})
export class InvoiceCreateComponent {
  invoiceForm: FormGroup;
  private _selectedStatus: string | undefined;

  constructor(
    private formBuilder: FormBuilder,
    private invoiceService: InvoiceService,
    private router: Router
  ) {
    this.invoiceForm = this.formBuilder.group({
      date: ['', Validators.required],
      status: ['', Validators.required],
      amount: ['', [Validators.required, Validators.min(0.01)]],
      customerId: ['', [Validators.required, Validators.min(1)]]
    });
  }

  get selectedStatus(): string | undefined {
    return this._selectedStatus;
  }

  onStatusChange(selectedStatus: string) {
    this._selectedStatus = selectedStatus;
  }

  onSubmit() {
    if (this.invoiceForm.valid) {
      const createInvoiceDto: CreateInvoiceDto = {
        date: this.invoiceForm.value.date,
        status: this.selectedStatus || '',
        amount: this.invoiceForm.value.amount,
        customerId: this.invoiceForm.value.customerId
      };

      this.invoiceService.createInvoice(createInvoiceDto).subscribe(result => {
        console.log('Invoice Created', result);
        //Navigate to the list of invoices
        this.router.navigate(['/invoices']);
      });
    } else {
      this.markFormGroupTouched(this.invoiceForm);
    }
  }

  markFormGroupTouched(formGroup: FormGroup) {
    Object.values(formGroup.controls).forEach(control => {
      control.markAsTouched();

      if (control instanceof FormGroup) {
        this.markFormGroupTouched(control);
      }
    });
  }
}


<h2>Create Invoice</h2>
<form [formGroup]="invoiceForm" (ngSubmit)="onSubmit()">
  <div>
    <label>Date:</label>
    <input type="date" formControlName="date">
    <app-validation-error [control]="invoiceForm.get('date')!"></app-validation-error>
  </div>
  <div>
    <app-invoice-status [selectedStatus]="selectedStatus" (statusChange)="onStatusChange($event)"></app-invoice-status>
    <app-validation-error [control]="invoiceForm.get('status')!"></app-validation-error>
  </div>
  <div>
    <label>Amount:</label>
    <input type="number" formControlName="amount">
    <app-validation-error [control]="invoiceForm.get('amount')!"></app-validation-error>
  </div>
  <div>
    <label>Customer ID:</label>
    <input type="number" formControlName="customerId">
    <app-validation-error [control]="invoiceForm.get('customerId')!"></app-validation-error>
  </div>
  <button type="submit">Create Invoice</button>
</form>
