import { Component, OnInit } from '@angular/core';
import { InvoiceService } from './services/invoice.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  constructor(private invoiceService: InvoiceService) { }
  title = 'Customer Invoices App';
  ngOnInit() {
    this.invoiceService.getInvoiceStatuses().subscribe();
  }
}

<header>
  <h1>{{ title }}</h1>
  <nav>
    <a routerLink="/invoices" routerLinkActive="active">Invoices</a>
    <a routerLink="/invoices/create" routerLinkActive="active">Create Invoice</a>
  </nav>
</header>

<router-outlet></router-outlet>

<footer>
  <p>© 2023 Customer Invoices App</p>
</footer>
